mapper.select:
select j.jobs_id, ( select comp_name from comp_tb where comp_id = j.comp_id) compName, ( select photo from comp_tb where comp_id = j.comp_id ) photo, title, career, education, position, r.skill_name1, r.skill_name2, r.skill_name3, address, end_date from jobs_tb j join required_skill_tb r on j.jobs_id = r.jobs_id where
address in
#{address}
and
career regexp #{jDto.career}
