mapper.select:
select j.jobs_id, ( select comp_name from comp_tb where comp_id = j.comp_id) compName, ( select photo from comp_tb where comp_id = j.comp_id ) photo, title, career, education, position, address, end_date, r.skill
ifnull(( select user_scrap_id from user_scrap_tb where user_id = #{userId} and jobs_id = j.jobs_id ),null) user_scrap_id
from jobs_tb j join required_skill_tb r on j.jobs_id = r.jobs_id where title regexp #{keyword} and career regexp #{keyword} and education regexp #{keyword} and position regexp #{keyword} and address regexp #{keyword} and skill regexp #{keyword}

select j.jobs_id, ( select comp_name from comp_tb where comp_id = j.comp_id) compName, ( select photo from comp_tb where comp_id = j.comp_id ) photo, title, career, education, position, address, end_date, r.skill from jobs_tb j join required_skill_tb r on j.jobs_id = r.jobs_id where
address in
#{address}
and
